import{_ as t}from"./plugin-vue_export-helper-DlAUqK2U.js";import{c as a,o as n,a as e}from"./app-wJXw8B29.js";const i={},o=e("h1",{id:"培养团队",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#培养团队"},[e("span",null,"培养团队")])],-1),r=e("p",null,"凭借个人英雄主义式的单打独斗并不是团队精神所倡导的。过分依靠个人能力会使团队变得不稳定，并且效率低下。作为一名管理者，必须思考如何将团队连接成一个整体。这需要靠全体成员的热情和士气作为纽带。为了有效地培养团队成员对于工作的热情，与你达成一致的目标，为了大家共同的理想去奋斗，这需要两个要素：",-1),l=e("ol",null,[e("li",null,"价值观。你们的工作应该是充实而有意义的工作。这需要管理者潜意识中去引导，让团队成员切身感受到肩负重任。别人可以不在乎你们做了什么，但是团队自身应该要有共同的价值观，在工作的过程中体会到团队创造的价值。"),e("li",null,"来自团队成员的认可。管理者要在人际上组织好团队，让团队成员间相互信任与敬佩，营造良好的工作环境。管理者应多听每一个来自团队的声音，友善地对待每一位成员。不同管理者的管理手段可以不同，并不是说只有塑造具有亲和力的形象才算是管理上的成功。但至少，管理者不能因为担任管理职务就沾沾自喜，对团队的成员指手画脚。无论是在学校的各级组织，还是在外的公司，成功管理者都会以平和的心态对待他们工作上的每一位同事，并且主动地给予能够给予的帮助。")],-1),c=[o,r,l];function p(s,d){return n(),a("div",null,c)}const h=t(i,[["render",p],["__file","pei-yang-tuan-dui.html.vue"]]),g=JSON.parse('{"path":"/sidebar/sjtu/fang-tan-ji/guan-li-zhe-de-zhi-hui/pei-yang-tuan-dui.html","title":"培养团队","lang":"zh-CN","frontmatter":{"description":"培养团队 凭借个人英雄主义式的单打独斗并不是团队精神所倡导的。过分依靠个人能力会使团队变得不稳定，并且效率低下。作为一名管理者，必须思考如何将团队连接成一个整体。这需要靠全体成员的热情和士气作为纽带。为了有效地培养团队成员对于工作的热情，与你达成一致的目标，为了大家共同的理想去奋斗，这需要两个要素： 价值观。你们的工作应该是充实而有意义的工作。这需要管...","head":[["meta",{"property":"og:url","content":"https://javabetter.cn/sidebar/sjtu/fang-tan-ji/guan-li-zhe-de-zhi-hui/pei-yang-tuan-dui.html"}],["meta",{"property":"og:site_name","content":"二哥的Java进阶之路"}],["meta",{"property":"og:title","content":"培养团队"}],["meta",{"property":"og:description","content":"培养团队 凭借个人英雄主义式的单打独斗并不是团队精神所倡导的。过分依靠个人能力会使团队变得不稳定，并且效率低下。作为一名管理者，必须思考如何将团队连接成一个整体。这需要靠全体成员的热情和士气作为纽带。为了有效地培养团队成员对于工作的热情，与你达成一致的目标，为了大家共同的理想去奋斗，这需要两个要素： 价值观。你们的工作应该是充实而有意义的工作。这需要管..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2024-11-28T13:37:17.000Z"}],["meta",{"property":"article:author","content":"沉默王二"}],["meta",{"property":"article:modified_time","content":"2024-11-28T13:37:17.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"培养团队\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2024-11-28T13:37:17.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"沉默王二\\",\\"url\\":\\"/about-the-author/\\"}]}"]]},"headers":[],"git":{"createdTime":1732801037000,"updatedTime":1732801037000,"contributors":[{"name":"沉默王二","email":"www.qing_gee@163.com","commits":1}]},"readingTime":{"minutes":1.44,"words":432},"filePathRelative":"sidebar/sjtu/fang-tan-ji/guan-li-zhe-de-zhi-hui/pei-yang-tuan-dui.md","localizedDate":"2024年11月28日","excerpt":"\\n<p>凭借个人英雄主义式的单打独斗并不是团队精神所倡导的。过分依靠个人能力会使团队变得不稳定，并且效率低下。作为一名管理者，必须思考如何将团队连接成一个整体。这需要靠全体成员的热情和士气作为纽带。为了有效地培养团队成员对于工作的热情，与你达成一致的目标，为了大家共同的理想去奋斗，这需要两个要素：</p>\\n<ol>\\n<li>价值观。你们的工作应该是充实而有意义的工作。这需要管理者潜意识中去引导，让团队成员切身感受到肩负重任。别人可以不在乎你们做了什么，但是团队自身应该要有共同的价值观，在工作的过程中体会到团队创造的价值。</li>\\n<li>来自团队成员的认可。管理者要在人际上组织好团队，让团队成员间相互信任与敬佩，营造良好的工作环境。管理者应多听每一个来自团队的声音，友善地对待每一位成员。不同管理者的管理手段可以不同，并不是说只有塑造具有亲和力的形象才算是管理上的成功。但至少，管理者不能因为担任管理职务就沾沾自喜，对团队的成员指手画脚。无论是在学校的各级组织，还是在外的公司，成功管理者都会以平和的心态对待他们工作上的每一位同事，并且主动地给予能够给予的帮助。</li>\\n</ol>","autoDesc":true}');export{h as comp,g as data};
